import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css"
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";

function Expenses(props) {

    const [selectedYear, setSelectedYear] = useState('2022')

    const yearSelectHandler = yearSelected => {
        setSelectedYear(yearSelected)
        console.log(yearSelected)
    }

    const filteredYear = props.items.filter(e => {
        return e.date.getFullYear().toString() === selectedYear
    })

    let expensesContent = <p>No Expenses</p>

    if (filteredYear.length > 0) {filteredYear.map(expense => 
        <ExpenseItem 
            key={expense.id}
            title={expense.title} 
            amount={expense.amount} 
            date={expense.date}/>)

    }

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selected={selectedYear} onYearSelect={yearSelectHandler} />
                {filteredYear.length === 0 && <p>No expenses</p>}
                {filteredYear.length > 0 && filteredYear.map(expense => 
                <ExpenseItem 
                    key={expense.id}
                    title={expense.title} 
                    amount={expense.amount} 
                    date={expense.date}/>)}
            </Card>
    </div>
    )
}

export default Expenses;
